mapper.select:
select
  id, username, sno, password, state, create_time, phone, type, avatar, department_id, dormitory_name, college_name
from department.t_user


        and id = #{id}


        and username = #{username}


        and sno = #{sno}


        and password = #{password}


        and state = #{state}


        and create_time = #{createTime}


        and phone = #{phone}


        and type = #{type}


        and avatar = #{avatar}


        and department_id = #{departmentId}


        and dormitory_name = #{dormitoryName}


        and college_name = #{collegeName}
